// Converter imagem baixada para Base64
const binaryData = $input.first().binary;
const imageKey = Object.keys(binaryData)[0];
const imageData = binaryData[imageKey];

return {
  json: {
    imageBase64: imageData.data,
    mimeType: imageData.mimeType || 'image/jpeg',
    fileName: imageData.fileName || 'receipt.jpg',
    mediaUrl: $('Webhook - Receber Mensagem').item.json.mediaUrl
  }
};

// Preparar prompt otimizado para análise de comprovantes bancários brasileiros
const prompt = `Analise esta imagem de comprovante bancário, recibo ou nota fiscal brasileira.

Extraia as seguintes informações em formato JSON válido (sem markdown, sem explicações):

{
  "amount": número (valor da transação em reais, apenas números),
  "type": "income" ou "expense" (receita ou despesa),
  "date": "YYYY-MM-DD" (data da transação),
  "description": "texto descritivo da transação",
  "category": "categoria" (ex: Alimentação, Transporte, Salário, Serviços, Saúde, etc.),
  "contextId": "HOME" ou "CLINIC" (pessoal ou clínica),
  "confidence": número de 0 a 1 (confiança na extração)
}

Regras importantes:
- Se for depósito, transferência recebida ou salário → type: "income"
- Se for pagamento, compra ou saque → type: "expense"
- Se não conseguir identificar a data, use a data atual
- Se não conseguir identificar o valor, use 0
- Se não conseguir identificar categoria, use "Outros"
- Se não conseguir identificar contexto, use "HOME"
- Retorne APENAS o JSON, sem markdown, sem explicações`;

return {
  json: {
    prompt: prompt,
    imageBase64: $('Converter para Base64').item.json.imageBase64,
    mimeType: $('Converter para Base64').item.json.mimeType
  }
};

// Extrai e processa JSON da resposta do GPT-4 Vision
const visionResponse = $input.first().json;

// Extrair conteúdo da resposta
let content = '';
try {
  if (visionResponse.choices && visionResponse.choices[0]) {
    content = visionResponse.choices[0].message?.content || '';
  } else if (visionResponse.content) {
    content = visionResponse.content;
  } else if (visionResponse.text) {
    content = visionResponse.text;
  } else {
    content = JSON.stringify(visionResponse);
  }
} catch (e) {
  console.error('Erro ao extrair conteúdo:', e);
  content = JSON.stringify(visionResponse);
}

// Limpar markdown se houver
let jsonString = content
  .replace(/```json\n?/gi, '')
  .replace(/```\n?/gi, '')
  .trim();

// Tentar extrair JSON
let extractedData;
try {
  // Procura por JSON no texto
  const jsonMatch = jsonString.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    extractedData = JSON.parse(jsonMatch[0]);
  } else {
    extractedData = JSON.parse(jsonString);
  }
} catch (error) {
  console.error('Erro ao parsear JSON:', error);
  console.log('Conteúdo recebido:', content.substring(0, 500));

  // Valores padrão se falhar
  extractedData = {
    amount: 0,
    type: "expense",
    date: new Date().toISOString().split('T')[0],
    description: "Transação via WhatsApp",
    category: "Outros",
    contextId: "HOME",
    confidence: 0
  };
}

// Valida e normaliza os dados
const result = {
  amount: parseFloat(extractedData.amount) || 0,
  type: (extractedData.type === "income" || extractedData.type === "receita") ? "income" : "expense",
  date: extractedData.date || new Date().toISOString().split('T')[0],
  description: extractedData.description || "Transação via WhatsApp",
  category: extractedData.category || "Outros",
  contextId: (extractedData.contextId === "CLINIC" || extractedData.contextId === "clinica") ? "CLINIC" : "HOME",
  confidence: parseFloat(extractedData.confidence) || 0.5,
  attachmentUrl: $('Webhook - Receber Mensagem').item.json.mediaUrl,
  rawResponse: content.substring(0, 200) // Para debug
};

// Validações adicionais
if (result.amount < 0) result.amount = Math.abs(result.amount);
if (!result.date.match(/^\d{4}-\d{2}-\d{2}$/)) {
  result.date = new Date().toISOString().split('T')[0];
}

return result;
