// Processa resposta do GPT-4 Vision e prepara dados
const visionResponse = $input.first().json;
const webhookData = $('Webhook - Receber Mensagem').item.json;

// Extrair conte√∫do
let content = '';
try {
  if (visionResponse.choices && visionResponse.choices[0]) {
    content = visionResponse.choices[0].message?.content || '';
  } else {
    content = JSON.stringify(visionResponse);
  }
} catch (e) {
  content = JSON.stringify(visionResponse);
}

// Limpar e parsear JSON
let jsonString = content.replace(/```json\n?/gi, '').replace(/```\n?/gi, '').trim();
let extractedData;

try {
  const jsonMatch = jsonString.match(/\{[\s\S]*\}/);
  extractedData = jsonMatch ? JSON.parse(jsonMatch[0]) : JSON.parse(jsonString);
} catch (error) {
  console.error('Erro ao parsear:', error);
  extractedData = {};
}

// Normalizar dados
const result = {
  // Dados b√°sicos
  amount: parseFloat(extractedData.amount) || 0,
  type: (extractedData.type === 'income' || extractedData.type === 'receita') ? 'income' : 'expense',
  date: extractedData.date || new Date().toISOString().split('T')[0],
  description: extractedData.description || 'Transa√ß√£o via WhatsApp',

  // Categoria com sistema inteligente
  category: extractedData.category || 'Outros',
  possibleCategories: extractedData.possibleCategories || [extractedData.category || 'Outros'],
  categoryConfidence: parseFloat(extractedData.categoryConfidence) || 0.5,
  needsCategoryConfirmation: (parseFloat(extractedData.categoryConfidence) || 0.5) < 0.7,

  // Contexto (sempre confirmar)
  contextId: (extractedData.contextId === 'CLINIC' || extractedData.contextId === 'clinica') ? 'CLINIC' : 'HOME',
  contextConfidence: parseFloat(extractedData.contextConfidence) || 0.3,
  needsContextConfirmation: true,

  // Dados adicionais
  payer: extractedData.payer || null,
  receiver: extractedData.receiver || null,
  transactionId: extractedData.transactionId || null,
  bank: extractedData.bank || null,
  account: extractedData.account || null,
  notes: extractedData.notes || null,

  // Metadados
  attachmentUrl: webhookData.mediaUrl,
  from: webhookData.from,
  fromName: webhookData.fromName,
  messageId: webhookData.messageId,
  confidence: parseFloat(extractedData.confidence) || 0.5
};

// Valida√ß√µes
if (result.amount < 0) result.amount = Math.abs(result.amount);
if (!result.date.match(/^\d{4}-\d{2}-\d{2}$/)) {
  result.date = new Date().toISOString().split('T')[0];
}

return result;

// Prepara mensagem de confirma√ß√£o para WhatsApp
const data = $input.first().json;

// Monta mensagem com dados extra√≠dos
let message = `üìã *Comprovante Recebido*\n\n`;
message += `üí∞ *Valor:* R$ ${data.amount.toFixed(2)}\n`;
message += `üìÖ *Data:* ${data.date}\n`;
message += `üìù *Descri√ß√£o:* ${data.description}\n`;
message += `üè∑Ô∏è *Tipo:* ${data.type === 'income' ? 'Receita' : 'Despesa'}\n\n`;

// Categoria (se precisa confirmar)
if (data.needsCategoryConfirmation && data.possibleCategories.length > 1) {
  message += `‚ùì *Categoria:* (Precisa confirmar)\n`;
  message += `Op√ß√µes:\n`;
  data.possibleCategories.forEach((cat, idx) => {
    message += `${idx + 1}. ${cat}\n`;
  });
  message += `\nResponda com o n√∫mero da categoria ou digite o nome.\n\n`;
} else {
  message += `üè∑Ô∏è *Categoria:* ${data.category}\n\n`;
}

// Sempre pedir contexto
message += `üìç *Para onde vai esta transa√ß√£o?*\n`;
message += `1. üè† HOME (Pessoal)\n`;
message += `2. üè• CLINIC (Cl√≠nica)\n\n`;
message += `Responda com 1 ou 2, ou digite HOME/CLINIC.\n\n`;

// Dados adicionais se dispon√≠veis
if (data.bank) message += `üè¶ Banco: ${data.bank}\n`;
if (data.payer) message += `üë§ Pagador: ${data.payer}\n`;
if (data.receiver) message += `üë§ Recebedor: ${data.receiver}\n`;

message += `\n---\n`;
message += `Confirme os dados acima para criar a transa√ß√£o.`;

return {
  json: {
    ...data,
    confirmationMessage: message
  }
};
